import React from 'react';
import {ThreeDots} from 'react-loader-spinner';


const Spinner = ({message}) => {
  return (
    <div  style={{display:'flex', flexDirection: 'column',justifyContent: 'center', width:'100%', height:'100%'}}>
      <ThreeDots 
        type="Circles"
        color="#00BFFF"
        height={50}
        width={200}
        text-ali
        ariaLabel='loading ...'
       />
       <p >{message}</p>
    </div>
  )
}

export default Spinner;
