import React from 'react';
import {ThreeDots} from 'react-loader-spinner';


const Spinner = ({message}) => {
  return (
    <div className="flex flex-col justify-center items-center w-full h-full" style={{display:'flex', flexDirection: 'column',}}>
      <ThreeDots 
        type="Circles"
        color="#00BFFF"
        height={50}
        width={200}
        className="n-5"
        ariaLabel='loading ...'
       />
       <p className="text-lg text-center px-2">{message}</p>
    </div>
  )
}

export default Spinner;
